import { useSelector, useDispatch } from "react-redux";
import { useState } from 'react';

import classes from "./Counter.module.css";

const Counter = () => {
  const [hidden, setHidden] = useState(false);

  const counter = useSelector((state) => state.counter);
  const dispatch = useDispatch();

  const buttonHandler = (type,amount) => {
    dispatch({type: type, amount: amount})
  }
  const toggleCounterHandler = (prevState) => {
    setHidden(!prevState);
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}</div>
      {!hidden && <div>
      <button onClick={() => buttonHandler("increment",1)}>
        Increment
      </button>
      <button onClick={() => buttonHandler("reset")}>
        Reset
      </button>
      <button onClick={() => buttonHandler("decrement",2)}>
        Decrement
      </button>
      </div>}
    </main>
  );
};

export default Counter;
