import { useSelector, useDispatch } from "react-redux";

import classes from "./Counter.module.css";

const Counter = () => {
  const counter = useSelector((state) => state.counter);
  const show = useSelector((state) => state.showCounter);
  const dispatch = useDispatch();

  const buttonHandler = (type, amount) => {
    dispatch({ type: type, amount: amount });
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {show ? (
        <div className={classes.value}>{counter}</div>
      ) : (
        <div className={classes.value}></div>
      )}
      <div>
        <button onClick={() => buttonHandler("increment", 1)}>Increment</button>
        <button onClick={() => buttonHandler("reset")}>Reset</button>
        <button onClick={() => buttonHandler("decrement", 2)}>Decrement</button>
      </div>
      <button onClick={() => buttonHandler("toggle")}>
        Toggle Counter Button
      </button>
    </main>
  );
};

export default Counter;
