import { useSelector, connect } from 'react-redux';
import classes from './Counter.module.css';
import store from '../store/index';

const Counter = () => {
  const counter= useSelector(state => state.counter);

  const toggleCounterHandler = (action) => {
    store.dispatch(action);
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}</div>
        <button onClick={() => toggleCounterHandler({type: 'increment'})}>Increment</button>
        <button onClick={() => toggleCounterHandler({type: 'reset'})}>Reset</button>
        <button onClick={() => toggleCounterHandler({type: 'decrement'})}>Decrenebt</button>
    </main>
  );
};

export default Counter;
