import { useSelector, useDispatch } from "react-redux";

import { counterActions } from '../store/index';
import classes from "./Counter.module.css";

const Counter = () => {
  const counter = useSelector((state) => state.counter);
  const show = useSelector((state) => state.showCounter);
  const dispatch = useDispatch();

  const buttonHandler = (type, amount) => {
    dispatch({ type, amount: amount });
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={show ? classes.value : classes.invi}>{counter}</div>
      <div>
        <button onClick={() => buttonHandler(counterActions.increment(), 1)}>Increment</button>
        <button onClick={() => buttonHandler("reset")}>Reset</button>
        <button onClick={() => buttonHandler("decrement", 2)}>Decrement</button>
      </div>
      <button onClick={() => buttonHandler("toggle")}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
