import { uiActions } from "./ui-slice";

export const sendCartData = async (cart, dispatch) => {
  dispatch(
    uiActions.showNotification({
      status: "pending",
      title: "Sending...",
      message: "Sending cart data.",
    })
  );

  try {
    const response = await fetch(
      "https://dummy-29e97-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json",
      { method: "PUT", body: JSON.stringify(cart) }
    );

    if (!response.ok) {
      throw new Error("Sending cart data failed.");
    }

    dispatch(
      uiActions.showNotification({
        status: "success",
        title: "Success!",
        message: "Sent cart data successfully.",
      })
    );
  } catch (error) {
    dispatch(
      uiActions.showNotification({
        status: "error",
        title: "Error!",
        message: "Sending cart data failed.",
      })
    );
  }
};
// Action function to fetch cart data
export const fetchCartData = async (dispatch) => {
  dispatch(
    uiActions.showNotification({
      status: "pending",
      title: "Fetching...",
      message: "Fetching cart data.",
    })
  );

  try {
    const response = await fetch(
      "https://dummy-29e97-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json"
    );

    if (!response.ok) {
      throw new Error("Fetching cart data failed.");
    }

    const data = await response.json();

    // Assuming `cartActions.replaceCart` is the action to replace the cart state
    dispatch(
      cartActions.replaceCart({
        items: data.items || [],
        totalQuantity: data.totalQuantity || 0,
      })
    );

    dispatch(
      uiActions.showNotification({
        status: "success",
        title: "Success!",
        message: "Fetched cart data successfully.",
      })
    );
  } catch (error) {
    dispatch(
      uiActions.showNotification({
        status: "error",
        title: "Error!",
        message: "Fetching cart data failed.",
      })
    );
  }
};
